// node_modules/@blaze-case-ai/blaze-engine/server/route/case-type-route.js

const express = require('express');
const router = express.Router();
const controller = require('@blaze-case-ai/blaze-engine/server/controller/case-type-controller');

// List all case-types
router.get('/case-types', controller.getCaseTypes);

// Get one by ID
router.get('/case-type/:caseTypeId', controller.getCaseType);

// Create a new case-type
router.post('/case-type', controller.createCaseType);
router.post('/case-type-pong', async(req,res) => {try{
    res.json({message:'ping'})
  }catch(err){
    res.status(500).json({ message: err.message });
  }
})
router.post('/controller-ping', controller.caseTypeControllerPing)

// Update existing
router.put('/case-type/:caseTypeId', controller.updateCaseType);

// Delete one
router.delete('/case-type/:caseTypeId', controller.deleteCaseType);

module.exports = router;
