const express = require('express');
const router = express.Router();
const componentController = require('../controller/component-controller');

// The controller should handle the correct path to the components directory.
// No need to define COMPONENTS_DIR here.
router.get('/component-controller-ping', componentController.ping);
router.get('/components', componentController.getComponents);
router.get('/component/:componentName', componentController.getComponent);
router.post('/component', componentController.createComponent);
router.put('/component/:componentName', componentController.updateComponent);
router.delete('/component/:componentName', componentController.deleteComponent);

module.exports = router;