const express = require('express');
const router = express.Router();
const controller = require('../controller/data-model-controller');

// List all data models
router.get('/data-models', controller.getDataModels);

// Get one data model by ID
router.get('/data-model/:dataModelId', controller.getDataModel);

// Create a new data model
router.post('/data-model', controller.createDataModel);

// Update existing data model
router.put('/data-model/:dataModelId', controller.updateDataModel);

// Delete a data model
router.delete('/data-model/:dataModelId', controller.deleteDataModel);

// Delete an attribute from a data model
router.delete('/data-model/:dataModelId/attribute/:attributeId', controller.deleteDataModelAttribute);

module.exports = router;